import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { addComment, deleteComment } from "../api/postApi";
import { formatDate } from "../utils/formatDate";
import ConfirmationModal from "./ConfirmationModal";

const CommentsSection = ({ comments, postId, setComments, userId }) => {
    const [commentContent, setCommentContent] = useState("");
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalContent, setModalContent] = useState({});

    const navigate = useNavigate();

    const handleAddComment = async () => {
      if (!commentContent.trim()) {
        alert("댓글 내용을 입력해주세요.");
        return;
      }

      try {
        const newComment = await addComment(postId, commentContent);
        console.log(comments);
        setComments((prev) => [...prev, newComment.comment]);
        setCommentContent("");
      } catch (error) {
        console.error("Error :", error);
      }
    };


    const handleDeleteComment = async (comment_id) => {
        setModalContent({
            title: "댓글 삭제",
            message: "댓글을 삭제하시겠습니까? 복구할 수 없습니다.",
            onConfirm: async () => {
              try {
                await deleteComment(postId, comment_id);
                setComments((prev) => prev.filter((comment) => comment.comment_id !== comment_id));
                setIsModalOpen(false);
              } catch (error) {
                console.error("Error:", error);
              }
            },
          });
          setIsModalOpen(true);
    };


    return (
      <div className="comments-section">
        <h2>댓글</h2>
        {comments.map((comment) => (
          <div key={comment.comment_id} className="comment-item">
            <p>{comment.comment_content}</p>
            <small>{formatDate(comment.create_at)}</small>
            {userId === comment.user_id && (
              <button onClick={() => handleDeleteComment(comment.comment_id)}>삭제</button>
            )}
          </div>
        ))}
        <textarea
          value={commentContent}
          onChange={(e) => setCommentContent(e.target.value)}
        />
        <button onClick={handleAddComment}>댓글 추가</button>
        {isModalOpen && (
        <ConfirmationModal
          show={isModalOpen}
          title={modalContent.title}
          message={modalContent.message}
          onConfirm={modalContent.onConfirm}
          onCancel={() => setIsModalOpen(false)}
        />
      )}
      </div>
    );
  };

  export default CommentsSection;
