import React, { use } from "react";
import { useNavigate } from "react-router-dom";
import { formatCount } from "../utils/formatCount";
import { formatDate } from "../utils/formatDate";
import { Container, Row, Col, Card, Image } from "react-bootstrap";
const PostItem = ({ post }) =>{
    const navigate =useNavigate();

    const handleClick = () =>{
        navigate(`/post?post_id=${post.post_id}`);
    };

    return (
      <Card
  className="post-item-card shadow-sm mb-4 mx-auto"
  onClick={handleClick}
  style={{
    cursor: "pointer",
    maxWidth: "800px", // 카드의 최대 너비를 설정
    width: "100%", // 화면 너비의 90%로 설정
  }}
>

      <Card.Body>
        <Card.Title className="mb-3">{post.post_title}</Card.Title>
        <Card.Text>
          <div className="d-flex justify-content-between text-muted small mb-3">
            <span>좋아요 {formatCount(post.likes_count)}</span>
            <span>댓글 {formatCount(post.comment_count)}</span>
            <span>조회수 {formatCount(post.view_count)}</span>
          </div>
          <div className="d-flex align-items-center mt-3">
            <Image
              src={post.profile_image}
              alt={`${post.nickname}의 프로필`}
              roundedCircle
              style={{ width: "40px", height: "40px", objectFit: "cover" }}
            />
            <div className="ms-3">
              <div className="fw-bold">{post.nickname}</div>
              <small className="text-muted">{formatDate(post.create_at)}</small>
            </div>
          </div>
        </Card.Text>
      </Card.Body>
    </Card>
    );
  };

const PostList = ({ posts }) => {
    return (
      <Container>
      <Row className="g-3 justify-content-center">
        {posts.map((post) => (
          <Col key={post.post_id} lg={7}>
            <PostItem post={post} />
          </Col>
        ))}
      </Row>
    </Container>
    )
}
export default PostList;
