import React, { use } from "react";
import { useNavigate } from "react-router-dom";
import { formatCount } from "../utils/formatCount";
import { formatDate } from "../utils/formatDate";
import { Container, Row, Col, Card, Image } from "react-bootstrap";
const PostItem = ({ post }) =>{
    const navigate =useNavigate();

    const handleClick = () =>{
        navigate(`/post?post_id=${post.post_id}`);
    };

    return (
      <div className="post-item card mb-3" onClick={handleClick} style={{ cursor: "pointer", width:"600px", margin:"0 auto" }}>
        <div className="card-body">
          <h5 className="card-title">{post.post_title}</h5>
          <div className="card-text mb-2">
            <span>좋아요 {formatCount(post.likes_count)}</span> ·{" "}
            <span>댓글 {formatCount(post.comment_count)}</span> ·{" "}
            <span>조회수 {formatCount(post.view_count)}</span>
          </div>
          <div className="card-text text-muted">
            <small>{formatDate(post.create_at)}</small>
          </div>
          <div className="d-flex align-items-center mt-3">
            <img
              className="rounded-circle me-2"
              src={post.profile_image}
              alt="Author Avatar"
              style={{ width: "40px", height: "40px", objectFit: "cover" }}
            />
            <span className="author-name">{post.nickname}</span>
          </div>
        </div>
      </div>
    );
  };

const PostList = ({ posts }) => {
    return (
      <Container>
      <Row className="g-3 justify-content-center">
        {posts.map((post) => (
          <Col key={post.post_id} md={8} lg={6}>
            <PostItem post={post} />
          </Col>
        ))}
      </Row>
    </Container>
    )
}
export default PostList;
