import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {  updateUserProfile, deleteUserAccount } from "../api/userApi";
import { deleteUserComments, deleteUserPosts } from "../api/postApi";
import { validateNickname, validateProfile } from "../utils/validators";
import { escapeHtml } from "../utils/escape";
import ConfirmationModal from "../components/ConfirmationModal";
import { fetchUserData } from "../utils/fetchUserData"
import ProfileImageUpload from "../components/ProfileImageUpload";
import Header from "../components/Header";
const EditUser = () =>{
    const [user, setUser]= useState(null);
    const [nickname, setNickname] = useState("");
    const [existingProfileImage, setExistingProfileImage] = useState(null);
    const [profileImage, setProfileImage] = useState(null);

    const [nicknameError, setNicknameError] = useState("");

    const [isModalOpen, setIsModalOpen] = useState(false);

    const navigate = useNavigate();

    const isFormValid =  nickname && !nicknameError;

    useEffect (()=>{
        const fetchData = async ()=>{
            try {
                const userInfo = await fetchUserData();
                if (userInfo && userInfo.userInfo) {
                    setUser(userInfo);
                    setNickname(userInfo.userInfo.nickname); 
                    setExistingProfileImage(userInfo.userInfo.profile_image);
                    setProfileImage(userInfo.userInfo.profile_image)
                } 
            } catch (error) {
                console.error("Error :", error);
            }
        }

        fetchData();
    },[]);


    const handleNicknameChange = (e) =>{
        const value = e.target.value;
        setNickname(value);

        const { valid, message } = validateNickname(value);
        setNicknameError(valid ? "" : message);
    }

    const handleProfileImageChange = (image) =>{
        setProfileImage(image);
    }

    const handleSubmit = async (e) =>{
        e.preventDefault();
        if(isFormValid){

            const formData = new FormData();
            formData.append("nickname",escapeHtml(nickname));
            formData.append("profileImage",profileImage);
            formData.append('user_id',user.userInfo.user_id);

            console.log(nickname,profileImage);

            try{
                const response = await updateUserProfile(formData);
                if(!response.success){
                    if(response.message.includes("중복된 닉네임 입니다.")){
                        setNicknameError("*중복된 닉네임입니다.");
                    } else{
                        alert("회원 정보 수정 실패");
                    }
                } else{
                    alert(response.message);
                    setTimeout(()=>{
                        navigate("/board");
                    },2000);
                }
            } catch (error) {
                console.error("Error :", error);
              }

        }

    }

    const handleWithdraw = () => {
        setIsModalOpen(true);
    };

    const handleConfirmWithdraw = async () => {
        try {
          await deleteUserComments(user.user_id);
          await deleteUserPosts(user.user_id);
          await deleteUserAccount(user.user_id);
          alert("회원 탈퇴가 완료되었습니다.");
          navigate("/login");
        } catch (error) {
          console.error("Error:", error);
          alert("회원 탈퇴에 실패했습니다.");
        }
        setIsModalOpen(false);
      };

      return (
        <>
        <Header title="아무말 대잔치" profileImage={user ? getImageUrl(user.userInfo.profile_image) : null} onBack={handleBack} />
        <div className="container">
          <h2>회원 정보 수정</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <ProfileImageUpload onImageChange={handleProfileImageChange} existingImage={existingProfileImage} />
            </div>

            <div className="form-group">
              <label htmlFor="nickname">닉네임*</label>
              <input
                type="text"
                id="nickname"
                value={nickname}
                onChange={handleNicknameChange}
                className="form-control"
              />
              {nicknameError && <p className="error-text">{nicknameError}</p>}
            </div>

            <button type="submit" className="btn btn-primary" disabled={!isFormValid}>
              수정하기
            </button>
          </form>

          <button className="btn btn-danger mt-3" onClick={handleWithdraw}>
            회원탈퇴
          </button>

          {isModalOpen && (
            <ConfirmationModal
              show={isModalOpen}
              title="회원탈퇴"
              message="작성된 게시글과 댓글은 모두 삭제됩니다."
              onConfirm={handleConfirmWithdraw}
              onCancel={() => setIsModalOpen(false)}
            />
          )}
        </div>
        </>
      );
};


export default EditUser;
