import React from "react";
import { formatCount } from "../utils/formatCount";
import { formatDate } from "../utils/formatDate";
import { Card, Button, Row, Col, Image } from "react-bootstrap";

const PostHeader = ({ post, likeCount, handleLike, userId, handleDeletePost, handleEditPost }) => {
    const profileImageUrl = post.profile_image;
    const postImageUrl = post.post_image;

    return (
        <Card className="mb-4 shadow-sm">
            <Card.Body>
            <Row className="align-items-center mb-3">
                <Col xs="auto">
                    <Image
                        src={profileImageUrl}
                        alt="작성자 프로필"
                        roundedCircle
                        style={{ width: "50px", height: "50px", objectFit: "cover" }}
                    />
                </Col>
                <Col>
                    <h5 className="mb-1">{post.nickname}</h5>
                    <small className="text-muted">{formatDate(post.create_at)}</small>
                </Col>

            </Row>
        {postImageUrl && (
          <div className="post-image-section">
            <img
              src={postImageUrl}
              alt="게시글 이미지"
              style={{
                maxWidth: "100%",
                height: "auto",
                borderRadius: "8px",
                marginTop: "16px",
              }}
            />
          </div>
        )}
         <div className="post-content">
                <p>{post.post_content}</p>
        </div>
        <div className="post-stats">
          <button className="stat-item like-btn" onClick={handleLike}>
            <span className="stat-number">{likeCount}</span>
            <span className="stat-label">좋아요</span>
          </button>
          <div className="stat-item">
            <span className="stat-number">{formatCount(post.view_count)}</span>
            <span className="stat-label">조회수</span>
          </div>
          <div className="stat-item">
            <span className="stat-number">{formatCount(post.comment_count)}</span>
            <span className="stat-label">댓글</span>
          </div>
        </div>
        {userId === post.user_id && (
          <div className="post-actions">
            <button onClick={handleEditPost} className="edit-btn">
              수정
            </button>
            <button onClick={handleDeletePost} className="delete-btn">
              삭제
            </button>
          </div>
        )}
        </Card.Body>
      </Card>
    );
  };

  export default PostHeader;
