import React, { useEffect, useState } from "react";
import { authCheck } from "../api/authCheckApi";
import { fetchPosts } from "../api/postApi";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import PostList from "../components/PostList";
import Button from "react-bootstrap/Button"; 
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";

const Board = () =>{
    const [posts, setPosts] =useState([]);
    const navigate = useNavigate();

    useEffect(()=>{
        const fetchData = async () =>{

            try{
            const isAuthenticated = await authCheck();
            if(!isAuthenticated) return ;

            window.history.pushState(null, "", window.location.href);

            const response = await fetchPosts();

            if(Array.isArray(response.posts) && response.posts.length !== 0){
                setPosts(response.posts);
            }
          } catch (err) {
            console.error("게시글 데이터를 불러오는 중 오류가 발생했습니다:", err);

          }

        };
        fetchData();

        const handlePopState = () => {
            window.history.pushState(null, "", window.location.href);
          };

          window.addEventListener("popstate", handlePopState);

          return () => {
            window.removeEventListener("popstate", handlePopState);
          };

    },[navigate]);

    const handleCreatePost = () =>{
        navigate("/create-post");
    }
    return (
        <>
        <Header title="아무말 대잔치" />
        <Container fluid className="mt-4">

        <Row className="justify-content-between align-items-center mb-4">
          <h1 className="d-flex justify-content-center">안녕하세요 게시판입니다.</h1>
          <Button variant="primary" onClick={handleCreatePost}>
            새 글 작성
          </Button>
        </Row>
          <PostList posts={posts} />
      </Container>
      </>
    );
}

export default Board;
