import React, { useEffect, useState } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import { fetchUserData } from "../utils/fetchUserData.js"; 
import { ConfirmationModal } from "../components/ConfirmationModal.js";
import {
    fetchPostDetails,
    fetchComments,
    deletePost,
    deleteComment,
    addComment,
    updatePostLikes,
    updateComment,
    updatePostViews,
    userLikeStatus,
  } from "../api/postApi.js";
import Header from "../components/Header";
import PostHeader from "../components/PostHeader";
import CommentsSection from "../components/CommentsSection"
import { getImageUrl } from "../api/userApi";
import { formatCount } from "../utils/formatCount";
import { formatDate } from "../utils/formatDate";





const DetailPost = () =>{
    const location = useLocation();
    const params = new URLSearchParams(location.search);
    const post_id = params.get("post_id");
    const navigate = useNavigate();
    const [post, setPost] = useState(null);
    const [comments, setComments] = useState([]);
    const [isEditing, setIsEditing] = useState(false);
    const [editingCommentId, setEditingCommentId] = useState(null);
    const [commentContent, setCommentContent] = useState("");
    const [likeCount, setLikeCount] = useState(0);
    const [currentUser, setCurrentUser] = useState(null);
    const [userId, setUserId] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalContent, setModalContent] = useState({});

    useEffect( ()=>{
        const fetchData = async () =>{
            const currentUser = await fetchUserData();
            if(currentUser) setCurrentUser(currentUser);

             try {
                const postDetails = await fetchPostDetails(post_id);
                await updatePostViews(post_id);
                console.log(postDetails);
                setPost(postDetails.posts[0]);
                setLikeCount(postDetails.posts[0].likes_count);
                setUserId(postDetails.user_id);

                const commentResults = await fetchComments(post_id);
                setComments(commentResults.comments);
            } catch (error) {
                console.error("Error:", error);
            }
        }
        fetchData();
    },[post_id]);

    const handleLike = async () =>{
        try {
            const result = await userLikeStatus(post_id);

            const liked = result.result.is_exist;

            if (liked) {
              setLikeCount((prev) => prev - 1);
            } else {
              setLikeCount((prev) => prev + 1);
            }

            await updatePostLikes(post_id, userId);
          } catch (error) {
            console.error("Error:", error);
          }
    };

    const handleDeletePost = async () => {
        setModalContent({
            title: "게시글 삭제",
            message: "게시글을 삭제하시겠습니까? 복구할 수 없습니다.",
            onConfirm: async () => {
              try {
                await deletePost(post_id);
                navigate("/board");
              } catch (error) {
                console.error("Error:", error);
              }
            },
          });
          setIsModalOpen(true);
      };

    const handleAddComment = async () => {
        if (!commentContent.trim()) {
          alert("댓글 내용을 입력해주세요.");
          return;
        }

        try {
          if (isEditing) {
            const updatedComment = await updateComment(post_id, editingCommentId, commentContent);
            setComments((prev) =>
              prev.map((comment) =>
                comment.comment_id === editingCommentId ? updatedComment.comments[0] : comment
              )
            );
            setIsEditing(false);
            setEditingCommentId(null);
          } else {
            const newComment = await addComment(post_id, commentContent);
            console.log("New Comment Data:", newComment.comment);
            setComments((prev) => [...prev,newComment.comment]);
          }
          setCommentContent("");
        } catch (error) {
          console.error("Error :", error);
        }
    };




    return (
        <>
        <Header title="아무말 대잔치" profileImage={currentUser ? getImageUrl(currentUser.userInfo.profile_image) : null} onBack={()=> navigate("/board")} />
        <div className="post-container">
        {post && (
          <PostHeader
            post={post}
            likeCount={likeCount}
            handleLike={handleLike}
            userId={userId}
            handleDeletePost={handleDeletePost}
          />
        )}
         <CommentsSection
          comments={comments}
          postId={post_id}
          setComments={setComments}
          userId={userId}
        />
          <textarea
            value={commentContent}
            onChange={(e) => setCommentContent(e.target.value)}
          />
          <button onClick={handleAddComment}>{isEditing ? "댓글 수정" : "댓글 추가"}</button>
        </div>
        </>
    )


}

export default DetailPost;
