import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getUserProfile, updateUserProfile, deleteUserComments, deleteUserPosts, deleteUserAccount } from "../api/userApi";
import { validateNickname, validateProfile } from "../utils/validators";
import { escapeHtml } from "../utils/escape";
import ConfirmationModal from "../components/ConfirmationModal";
import { fetchUserData } from "../utils/fetchUserData"

const EditUser = () =>{
    const [user, setUser]= useState(null);
    const [nickname, setNickname] = useState("");
    const [profileImage, setProfileImage] = useState(null);
    const [profileImagePreview, setProfileImagePreview] = useState(null);
    const [nicknameError, setNicknameError] = useState("");
    const [profileImageError, setProfileImageError] = useState("");
    const [isModalOpen, setIsModalOpen] = useState(false);

    const navigate = useNavigate();

    const isFormValid = profileImage && nicknameError && !nicknameError && !profileImageError;

    useEffect (()=>{
        const fetchData = async ()=>{
            try {
                const userInfo = await fetchUserData();
                if (userInfo && userInfo.userInfo) {
                    setUser(userInfo);
                    setNickname(userInfo.userInfo.nickname); 
                    setProfileImage(userInfo.userInfo.profileImage); 
                } 
            } catch (error) {
                console.error("Error :", error);
            }
        }

        fetchData();
    },[]);


    const handleNicknameChange = (e) =>{
        const value = e.target.value;
        setNickname(value);

        const { valid, message } = validateNickname(value);
        setNicknameError(valid ? "" : message);
    }

    const handleProfileImageChange = (image) =>{
        setProfileImage(image);
        const { valid, message } = validateProfile(image);
        setProfileImageError(valid ? "" : message);

    }

    const handleSubmit = async (e) =>{
        e.preventDefault();
        if(isFormValid){

            const formData = new FormData();
            formData.append("nickname",escapeHtml(nickname));
            formData.append("profileImage",profileImage);

            try{
                const response = await updateUserProfile(formData);
                if(!response.success){
                    if(response.message.includes("중복된 닉네임 입니다.")){
                        setNicknameError("*중복된 닉네임입니다.");
                    } else{
                        alert("회원 정보 수정 실패");
                    }
                } else{
                    alert(response.message);
                    setTimeout(()=>{
                        navigate("/board");
                    },2000);
                }
            } catch (error) {
                console.error("Error :", error);
              }

        }

    }

}

export default EditUser;
