import React, { useEffect, useState } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import { fetchUserData } from "../utils/fetchUserData.js"; 
import { createModal } from "../components/Modal";
import {
    fetchPostDetails,
    fetchComments,
    deletePost,
    deleteComment,
    addComment,
    updatePostLikes,
    updateComment,
    updatePostViews,
    userLikeStatus,
  } from "../api/postApi.js";
import Header from "../components/Header";
import { getImageUrl } from "../api/userApi";
import { formatCount } from "../utils/formatCount";
import { formatDate } from "../utils/formatDate";





const DetailPost = () =>{
    const location = useLocation();
    const params = new URLSearchParams(location.search);
    const post_id = params.get("post_id");
    const navigate = useNavigate();
    const [post, setPost] = useState(null);
    const [comments, setComments] = useState([]);
    const [isEditing, setIsEditing] = useState(false);
    const [editingCommentId, setEditingCommentId] = useState(null);
    const [commentContent, setCommentContent] = useState("");
    const [likeCount, setLikeCount] = useState(0);
    const [currentUser, setCurrentUser] = useState(null);
    const [userInfo, setUserInfo] = useState(null);

    useEffect( ()=>{
        const fetchData = async () =>{
            const userInfo = await fetchUserData();
            if(userInfo) setCurrentUser(userInfo);

             try {
                const postDetails = await fetchPostDetails(post_id);
                await updatePostViews(post_id);
                console.log(post_id);
                setPost(postDetails.posts[0]);
                setLikeCount(postDetails.posts[0].likes_count);
                setUserInfo(postDetails.user);

                const commentResults = await fetchComments(post_id);
                setComments(commentResults.comments);
            } catch (error) {
                console.error("Error:", error);
            }
        }
        fetchData();
    },[post_id]);

    const handleLike = async () =>{
        try {
            const result = await userLikeStatus(post_id);
            const liked = result.result.is_exist;

            if (liked) {
              setLikeCount((prev) => prev - 1);
            } else {
              setLikeCount((prev) => prev + 1);
            }

            await updatePostLikes(post_id, userInfo.id);
          } catch (error) {
            console.error("Error:", error);
          }
    };

    const handleDeletePost = async () => {
        const { modal, confirmButton } = createModal({
          title: "게시글 삭제",
          message: "게시글을 삭제하시겠습니까? 복구할 수 없습니다.",
        });

        confirmButton.addEventListener("click", async () => {
          try {
            await deletePost(post_id);
            navigate("/board");
          } catch (error) {
            console.error("Error :", error);
          }
        });
      };

    const handleAddComment = async () => {
        if (!commentContent.trim()) {
          alert("댓글 내용을 입력해주세요.");
          return;
        }

        try {
          if (isEditing) {
            const updatedComment = await updateComment(post_id, editingCommentId, commentContent);
            setComments((prev) =>
              prev.map((comment) =>
                comment.comment_id === editingCommentId ? updatedComment.comments[0] : comment
              )
            );
            setIsEditing(false);
            setEditingCommentId(null);
          } else {
            const newComment = await addComment(post_id, commentContent);
            setComments((prev) => [...prev, newComment.comments[0]]);
          }
          setCommentContent("");
        } catch (error) {
          console.error("Error :", error);
        }
    };

    const handleDeleteComment = async (comment_id) => {
        const { modal, confirmButton } = createModal({
          title: "댓글 삭제",
          message: "댓글을 삭제하시겠습니까? 복구할 수 없습니다.",
        });

        confirmButton.addEventListener("click", async () => {
          try {
            await deleteComment(post_id, comment_id);
            setComments((prev) => prev.filter((comment) => comment.comment_id !== comment_id));
          } catch (error) {
            console.error("Error :", error);
          }
        });
    };


    return (
        <>
        <Header title="아무말 대잔치" profileImage={currentUser ? getImageUrl(currentUser.userInfo.profile_image) : null} onBack={()=> navigate("/board")} />
        <div className="post-container">
        {post && (
          <>
            <div className="post-header">
              <h1>{post.post_title}</h1>
              <div className="post-stats">
                <span>조회수: {formatCount(post.view_count)}</span>
                <span>좋아요: {likeCount}</span>
                <button onClick={handleLike}>좋아요</button>
              </div>
            </div>
            <p>{post.post_content}</p>
            {userInfo?.id === post.user_id && (
              <div>
                <button onClick={handleDeletePost}>삭제</button>
              </div>
            )}
          </>
        )}
        <div className="comments-section">
          <h2>댓글</h2>
          {comments.map((comment) => (
            <div key={comment.comment_id} className="comment-item">
              <p>{comment.comment_content}</p>
              <small>{formatDate(comment.create_at)}</small>
              {userInfo?.id === comment.user_id && (
                <button onClick={() => handleDeleteComment(comment.comment_id)}>삭제</button>
              )}
            </div>
          ))}
          <textarea
            value={commentContent}
            onChange={(e) => setCommentContent(e.target.value)}
          />
          <button onClick={handleAddComment}>{isEditing ? "댓글 수정" : "댓글 추가"}</button>
        </div>
      </div>
        </>
    )


}

export default DetailPost;
