import React, { use } from "react";
import { useNavigate } from "react-router-dom";
import { formatCount } from "../utils/formatCount";
import { formatDate } from "../utils/formatDate";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

const PostItem = ({ post }) =>{
    const navigate =useNavigate();

    const handleClick = () =>{
        navigate(`/detail-post?post_id =${post.post_id}`);
    };

    return (
        <div className="post-item" onClick={handleClick}>
          <div className="post-header">
            <h2 className="post-title">{post.post_title}</h2>
          </div>
          <div className="post-details">
            <div className="post-details2">
              <span>좋아요 {formatCount(post.likes_count)}</span>
              <span>댓글 {formatCount(post.comment_count)}</span>
              <span>조회수 {formatCount(post.view_count)}</span>
            </div>
            <span className="post-date">{formatDate(post.create_at)}</span>
          </div>
          <div className="post-footer">
            <img
              className="author-avatar"
              src={post.profile_image}
              alt="Author Avatar"
            />
            <span className="author-name">{post.nickname}</span>
          </div>
        </div>
      );
    };

const PostList = ({ posts }) => {
    return (
        <div id ="postTableBody">
            {posts.map((post) => (
                <PostItem key={post.post_id} post={post} />
            ))}
        </div>
    )
}
export default PostList;
