import React from "react";
import { formatCount } from "../utils/formatCount";
import { formatDate } from "../utils/formatDate";
import { Card, Button, Row, Col, Image } from "react-bootstrap";

const PostHeader = ({ post, likeCount, handleLike, userId, handleDeletePost, handleEditPost }) => {
    const profileImageUrl = post.profile_image;
    const postImageUrl = post.post_image;

    return (
        <Card className="mb-4 shadow-sm">
            <Card.Body>
            <Row className="align-items-center mb-3">
                <Col xs="auto">
                    <Image
                        src={profileImageUrl}
                        alt="작성자 프로필"
                        roundedCircle
                        style={{ width: "50px", height: "50px", objectFit: "cover" }}
                    />
                </Col>
                <Col>
                    <h5 className="mb-1">{post.nickname}</h5>
                    <small className="text-muted">{formatDate(post.create_at)}</small>
                </Col>
            </Row>
            <Card.Title className="mb-3">{post.post_title}</Card.Title>
            {postImageUrl && (
                    <div className="mb-3">
                        <Image
                            src={postImageUrl}
                            alt="게시글 이미지"
                            fluid
                            rounded
                        />
                    </div>
            )}
        <Card.Text className="mb-4">{post.post_content}</Card.Text>
        <div className="d-flex justify-content-between align-items-center">
            <Button variant="outline-primary" onClick={handleLike} className="me-3">
                좋아요 <span className="badge bg-primary ms-2">{likeCount}</span>
            </Button>
            <div className="text-muted">
                <span className="me-4">조회수: {formatCount(post.view_count)}</span>
                <span>댓글: {formatCount(post.comment_count)}</span>
            </div>
        </div>
        {userId === post.user_id && (
            <div className="d-flex justify-content-end mt-3">
                <Button variant="outline-secondary" onClick={handleEditPost} className="me-2">
                    수정
                </Button>
                <Button variant="outline-danger" onClick={handleDeletePost}>
                    삭제
                </Button>
            </div>
        )}
        </Card.Body>
      </Card>
    );
  };

  export default PostHeader;
