import React, { useState } from "react";
import Header from "../components/Header";
import { validateEmail, validatePassword } from "../utils/validators.js";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import "../styles/login-style.css"

const Login = () =>{
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [emailError, setEmailError] = useState("");
    const [passwordError, setPasswordError] = useState("");

    const isFormValid = email && password && !emailError && !passwordError;

    const handleEmailChange = (e) =>{
        const value = e.target.value;
        setEmail(value);

        const { valid, message } = validateEmail(value);
        setEmailError(valid ? "" : message);
    };

    const handlePasswordChange = (e) =>{
        const value =e.target.value;
        setPassword(value);

        const { valid, message } = validatePassword(value);
        setPasswordError(valid ? "" : message);

    };

    const handleSubmit = async (e) =>{
        e.preventDefault();
        if(isFormValid){
            try {
                const result = await login(email, password);

                if (result.message ==="*비밀번호가 다릅니다.") {
                    passwordHelper.textContent = "*존재하지 않은 이메일 또는 비밀번호입니다.";
                    emailHelper.textContent = "*존재하지 않은 이메일 또는 비밀번호입니다."
                    passwordHelper.style.visibility = "visible";
                    emailHelper.style.visibility = "visible";
                } else if(result.message ==="*이메일이 존재하지않습니다."){
                    passwordHelper.textContent = "*존재하지 않은 이메일 또는 비밀번호입니다.";
                    emailHelper.textContent = "*존재하지 않은 이메일 또는 비밀번호입니다."
                    emailHelper.style.visibility = "visible";
                    passwordHelper.style.visibility = "visible";
                } else{
                    window.location.href='board';
                }
            } catch (error) {
                console.error('로그인 요청 중 오류 발생:', error);
                alert('로그인 중 오류가 발생하였습니다. 다시 시도해주세요.');
             }
        }
    };

    return (
        <>
        {/* Header 컴포넌트 */}
        <Header title="아무말 대잔치" />
        <Container fluid className="d-flex flex-column align-items-center custom-container " >
          <h2 className="text-center mb-4 " >로그인</h2>
          <Row className="justify-content-center fixed-row">
              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3" controlId="email">
                  <Form.Label >이메일</Form.Label>
                  <Form.Control
                    type="email"
                    placeholder="이메일을 입력하세요"
                    value={email}
                    onChange={handleEmailChange}
                    isInvalid={!!emailError}
                  />
                  <Form.Control.Feedback type="invalid">
                  {emailError || " "}
                  </Form.Control.Feedback>
                </Form.Group>

                <Form.Group className="mb-3" controlId="password">
                  <Form.Label>비밀번호</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="비밀번호를 입력하세요"
                    value={password}
                    onChange={handlePasswordChange}
                    isInvalid={!!passwordError}
                  />
                  <Form.Control.Feedback type="invalid" >
                    {passwordError}
                  </Form.Control.Feedback>
                </Form.Group>

                <Button
                  variant="primary"
                  type="submit"
                  size="lg"
                  className="w-100 mb-3"
                  disabled={!isFormValid}
                >
                  로그인
                </Button>
              </Form>
              <Button
                variant="success"
                size="lg"
                className="w-100"
                onClick={() => (window.location.href = "/register")}
              >
                회원가입
              </Button>
          </Row>
        </Container>
        </>
      );
    };

    export default Login;
