import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { validatePostTitle, validatePostContent } from "../utils/validators";
import { fetchPostDetails, updatePost, fetchResource } from "../api/postApi";
import { escapeHtml } from "../utils/escape";
import { fetchUserData } from "../utils/fetchUserData.js"; 
import Header from "../components/Header";
import { getImageUrl } from "../api/userApi";


const EditPost = () =>{
    const [user, setUser] = useState(null);
    const [postTitle, setPostTitle] = useState("");
    const [postContent, setPostContent] = useState("");
    const [postImage, setPostImage] = useState(null);
    const [existingFileName, setExistingFileName] = useState("이미지를 선택하세요.");
    const [contentValid, setContentValid] = useState(false);
    const [titleValid, setTitleValid] = useState(false);
    const [titleError, setTitleError] = useState("");
    const [contentError, setContentError] = useState("");

    const location = useLocation();
    const navigate = useNavigate();
    const params = new URLSearchParams(location.search);
    const postId = params.get("post_id");

    const isFormValid = postTitle && postContent && !titleError && !contentError;

    useEffect(() => {
        const fetchData = async () =>{

        try{
            const userInfo = await fetchUserData();
            if(userInfo) setUser(userInfo);
            const postData = await fetchPostDetails(postId);
            const post = postData.posts[0];
            setPostTitle(post.post_title);
            setPostContent(post.post_content);

            if (post.post_image) {
                const response = await fetchResource(post.post_image);
                const blob = await response.blob();
                const fileName = decodeURIComponent(post.post_image.match(/[^-]+$/)[0]);
                setExistingFileName(fileName);
                setPostImage(new File([blob], fileName, { type: blob.type }));
              }

         } catch (err) {
        console.error("게시글 데이터를 불러오는 중 오류가 발생했습니다:", err);

        }
        }
        fetchData();
    }, [postId]);

    const handleTitleChange = (e) => {
        const value = e.target.value;
        setPostTitle(value);

        const {valid, message} = validatePostTitle(value);
        setTitleError(valid ? "" : message);
    };

    const handleContentChange = (e) => {
        const value = e.target.value;
        setPostContent(value);

        const {valid, message} = validatePostTitle(value);

        setContentError(valid ? "" : message);
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
          setPostImage(file);
          setExistingFileName(file.name);
        }
    };



    const handleDeleteImage = () => {
        setPostImage(null);
        setExistingFileName("이미지를 선택하세요.");
    };
    const handleSubmit = async (e) => {
        e.preventDefault();

        if(isFormValid){

        const formData = new FormData();
        formData.append("postTitle", escapeHtml(postTitle));
        formData.append("postContent", escapeHtml(postContent));

        if (postImage) {
          formData.append("postImage", postImage);
          formData.append("postDelete", false);
        } else {
          formData.append("postDelete", true);
        }

        try {
          const result = await updatePost(formData, postId);
          if (result.success) {
            navigate(`/detail-post?post_id=${postId}`);
          } else {
            alert("게시글 수정에 실패했습니다.");
          }
        } catch (error) {
          console.error("Error updating post:", error);
          alert("서버 오류가 발생했습니다. 다시 시도해주세요.");
        }
        }
      };

    const handleBack = () => {
        navigate(-1); 
      }

      return (
        <>
         <Header title="아무말 대잔치" profileImage={user ? getImageUrl(user.userInfo.profile_image) : null} onBack={handleBack} />
        <div className="edit-post-container">
          <h2>게시글 수정</h2>
          <form onSubmit={handleSubmit}>
            <div>
              <label htmlFor="postTitle">제목*</label>
              <input
                type="text"
                id="postTitle"
                value={postTitle}
                onChange={(e) => setPostTitle(e.target.value)}
              />
              {!titleValid && <p className="helper-text">제목을 입력해주세요.</p>}
            </div>

            <div>
              <label htmlFor="postContent">내용*</label>
              <textarea
                id="postContent"
                value={postContent}
                onChange={(e) => setPostContent(e.target.value)}
              />
              {!contentValid && <p className="helper-text">내용을 입력해주세요.</p>}
            </div>

            <div>
              <label htmlFor="postImage">이미지</label>
              <div className="post-image">
                <input
                  type="file"
                  id="postImage"
                  accept="image/*"
                  onChange={handleFileChange}
                  style={{ display: "none" }}
                />
                <button type="button" onClick={() => document.getElementById("postImage").click()}>
                  파일 선택
                </button>
                <span>{existingFileName}</span>
              </div>
              <button type="button" onClick={handleDeleteImage}>
                이미지 삭제
              </button>
            </div>

            <button type="submit" disabled={!contentValid || !titleValid}>
              수정하기
            </button>
          </form>
        </div>
        </>
      );
    };

export default EditPost;
